import { forwardRef } from "react";
import styled from "styled-components";

const Container = styled.div`
  margin-left:2rem;
  margin-right:2rem;
  margin-bottom:4rem;
  background-color: pink;
  padding: 24px;
  display: flex;
  justify-content: flex-start;
  align-items: center;
`;


const Content = styled.div`
  overflow: hidden;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
  width: 100%;
  height: 100%;
`;

const Text = styled.div`
  font-family:'Binggrae';
  font-size:1.27rem;
`

const RadioButtonLabel = styled.label`
  position: absolute;
  top: 25%;
  left: 4px;
  width: 24px;
  height: 24px;
  border-radius: 50%;
  background: white;
  border: 1px solid #bebebe;
`;

const RadioButton = styled.input`
  opacity: 0;
  z-index: 1;
  border-radius: 50%;
  width: 24px;
  height: 24px;
  margin-right: 10px;

  &:hover ~ ${RadioButtonLabel} {
    background: #bebebe;
    &::after {
      content: "";
      display: block;
      border-radius: 50%;
      width: 12px;
      height: 12px;
      margin: 6px;
      background: #eeeeee;
    }
  }
  ${(props) =>
    props.checked &&
    ` 
    &:checked + ${RadioButtonLabel} {
      background: #db7290;
      border: 1px solid #db7290;

      &::after {
        content: "";
        display: block;
        border-radius: 50%;
        width: 12px;
        height: 12px;
        margin: 5px;
        box-shadow: 1px 3px 3px 1px rgba(0, 0, 0, 0.1);
        background: white;
      }
    }
  `}
`;

const Question = forwardRef(
  (
    {
      invisible,
      qitemNo,
      question,
      answer01,
      answer02,
      answerScore01,
      answerScore02,
      onChange: handleChange,
      initialValue,
    },
    ref
  ) => {
    return (
      <div
        className="form-group"
        style={{
          display: invisible ? "none" : "block",
        }}
        key={`question${qitemNo}`}
      >

        {/* <div className="card bg-light py-4"> */}

        <Container>
          <div className="card-body text-center">
            {question}
            <div>

            <div className="form-check form-check-inline">
                <label className="form-check-label" style={{fontFamily:'Binggrae-Bold'}}>
                  <RadioButton
                    ref={ref}
                    type="radio"
                    name={`answers[${qitemNo - 1}]`}
                    className="form-check-input"
                    value={answerScore01}
                    onChange={() => {
                      if (typeof handleChange === "function") {
                        handleChange(qitemNo, answerScore01);
                      }
                    }}
                    defaultChecked={answerScore01 === initialValue}
                  />
                  <RadioButtonLabel />
                  <Text>{answer01}</Text>
                </label>
              </div>

              {/* <div className="form-check form-check-inline">
                <label className="form-check-label" style={{fontFamily:'Binggrae-Bold'}}>
                  <input
                    ref={ref}
                    type="radio"
                    name={`answers[${qitemNo - 1}]`}
                    className="form-check-input"
                    value={answerScore01}
                    onChange={() => {
                      if (typeof handleChange === "function") {
                        handleChange(qitemNo, answerScore01);
                      }
                    }}
                    defaultChecked={answerScore01 === initialValue}
                  />
                  {answer01}
                </label>
              </div> */}


              <div className="form-check form-check-inline">
                <label className="form-check-label" style={{fontFamily:'Binggrae-Bold'}}>
                  <input
                    ref={ref}
                    type="radio"
                    name={`answers[${qitemNo - 1}]`}
                    className="form-check-input"
                    value={answerScore02}
                    onChange={() => {
                      if (typeof handleChange === "function") {
                        handleChange(qitemNo, answerScore02);
                      }
                    }}
                    defaultChecked={answerScore02 === initialValue}
                  />
                  {answer02}
                </label>
              </div>



            </div>
          </div>
        {/* </div> */}
        </Container>
      </div>
    );
  }
);

export default Question;
