import React, { useState } from 'react';
import styled from 'styled-components';

import Modal from '../components/ModalComponent';


//type에 따라서 초록 버튼인지 회색 테두리 버튼인지 결정하게 수정하면 편함
// 맨 처음 시작하기 버튼
const Button = styled.button`
    width: 29.2rem;
    height: 7rem;
    border-radius: 1.5rem;
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    background-color: ${props => props.type === 'button' ? 'linear-gradient(120deg, #f093fb 0%, #f5576c 100%)' : props.theme.white};
    cursor:pointer;
    margin : 1.5rem;
    display: inline-flex;
    font-family:'Binggrae-Bold';
    color:white;
    display:flex;
    justify-content:center;
    align-items:center;
    font-weight: 400;
    font-size:2.0rem;
    text-align:center;
    border:none;

    &:disabled {
        background: white;
        color: orange;
        cursor: not-allowed;
    }
`

function ModalContainer({ name, gender, disabled, type, onClick }) {
    const [modalOpen, setModalOpen] = useState(false)
    const modalClose = () => {
        setModalOpen(!modalOpen)

    }

    return (
        <>
        <Button type={type} disabled={disabled} onClick={modalClose}>Click!</Button>
        { modalOpen && 
            <Modal 
                name={name}
                gender={gender}
                type={type} 
                onClick={onClick}
                modalClose={modalClose}
            />
        }
        </>

    )
}

export default ModalContainer
